/* Altitude hold controller 50 Hz */

double
altitude_hold_50(double h_f, double h_c){
    static double y = 0.0;
    static double Ts_h = 1.0/50.0;
    static double integrator = 532.2730285;

    if ((h_f - h_c) < -50) {
        // Output
        y = Vz_c;
    }
    else if ((h_f - h_c) > 50) {
        // Output
        y = -Vz_c;
    }
    else {
        // Output
        y = Kp_h * (h_f - h_c) + Ki_h * integrator;
        // State
        integrator += Ts_h * (h_f - h_c);
    }

    return y;
}

/* Altitude hold controller 33 Hz */

double
altitude_hold_33(double h_f, double h_c){
    static double y = 0.0;
    static double Ts_h = 1.0/33.0;
    static double integrator = 532.2730285;

    if ((h_f - h_c) < -50) {
        // Output
        y = Vz_c;
    }
    else if ((h_f - h_c) > 50) {
        // Output
        y = -Vz_c;
    }
    else {
        // Output
        y = Kp_h * (h_f - h_c) + Ki_h * integrator;
        // State
        integrator += Ts_h * (h_f - h_c);
    }

    return y;
}

